/* Scroll Navbar */

$(window).scroll(function(){

  var b = $(window).scrollTop();

  if( b > 70 ){
    $(".navbar.fixed-top").addClass("scroll-fixed-navbar");
  } else {
    $(".navbar.fixed-top").removeClass("scroll-fixed-navbar");

  }
});

$(document).ready(function() {
  /* toggle button */
  $('#nav-icon3').click(function () {
    $(this).toggleClass('open');
    $('.menu-mobile').toggleClass('open');
  });

  $('.navbar-nav li').click(function () {
    $('#nav-icon3').removeClass('open');
  });
  /* end */

  /* navbar collaps after click (mobile) */
  $(".navbar-collapse").on("click", "a", null, function () {
    $(".navbar-collapse").collapse('hide');
  });
  /* end */

  /* slow anhor */
  $(".goTo").on("click", function (event) {
    event.preventDefault();
    var id = $(this).attr('href'),
      top = $(id).offset().top;
    $('body,html').animate({scrollTop: top}, 1000);
  });
  /* end */

});

// slider
$(document).ready(function(){
  $('.product-slider').slick({
    speed: 300,
    slidesToShow: 3,
    slidesToScroll: 1,
    prevArrow: '<button class="slide-arrow next-arrow"></button>',
    nextArrow: '<button class="slide-arrow prev-arrow"></button>',
    responsive: [
      {
        breakpoint: 1025,
        settings: {
          slidesToShow: 2
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  });
});
